import ArtImageTile from '../ArtImageTile'
import ArtDescription from '../ArtDescription';
import { useParams, Route, Switch } from 'react-router-dom';

function GalleryView({ galleries }) {
    let { galleryId } = useParams();
    let gallery = galleries.find((gallery) => gallery.gallerynumber === galleryId);
    console.log(gallery);

    return (
        <Switch>
            // create ArtImageTiles for each object in the gallery
            <Route exact path={`/galleries/${galleryId}`}>
                <h1>{gallery.name}</h1>
                {
                    gallery.objects.map((object) => {
                        return (
                            <ArtImageTile galleryId={galleryId} object={object} />
                        )
                    })
                }
            </Route>

            // create Routes to descriptions for each object in the gallery
            {
                gallery.objects.map((object) => {
                    return (
                        <Route exact path={`/galleries/${galleryId}/art/${object.id}`}>
                            <ArtDescription gallery={gallery} object={object} />
                        </Route>
                    )
                })
            }


        </Switch>


    );
}

export default GalleryView;
