import ArtImageTile from '../ArtImageTile'
import { useParams } from 'react-router-dom';

function GalleryView({ galleries }) {
    let { id } = useParams();
    let gallery = galleries.find((gallery) => gallery.gallerynumber === id);
    console.log(gallery);
    let artworks = gallery.objects;

    return (
        <div>
            <h1>{gallery.name}</h1>
            {
                // for all art in current gallery, create ArtImageTime
                artworks.map((artwork) => {
                    return (
                        <ArtImageTile galleries={galleries} />
                    )
                })
            }
        </div>
    );
}

export default GalleryView;
